  SELECT a.user_id, a.user_name, a.points, b.lecture_no, b.user_id as teacher, b.lecture_writer,b.category_name, b.lecture_title,
        b.lecture_due, b.lecture_level, b.lecture_saleRate, b.lecture_saledue, b.lecture_price, b.lecture_regdate, b.lecture_coverimg,
