Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (1,'[탈퇴] 인프런을 탈퇴하고 싶어요.','대시보드 > 계정정보 설정에서 ''탈퇴''가 가능해요.<br><br>※ 탈퇴할 경우 해당 계정의 모든 데이터가 삭제되며 복구가 불가능하니 신중히 결정하시는 것을 권장 드려요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (26,'[계정] 구매했던 강의가 전부 사라졌어요.','잠깐! 지금 로그인하신 계정으로 구매하신 것이 맞으신가요?<br><br>인프런은 여러 개의 계정을 동시에 사용할 수 있어요. <br>소유하신 다른 계정이 있다면 확인 부탁드려요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (2,'[계정] 이메일 계정을 변경하고 싶어요.','1. [대시보드]-[계정정보]-[이메일]에서 새로운 이메일 입력<br>2. 안내된 인증 절차를 거치면 계정 변경 완료');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (3,'[계정] 비밀번호를 찾고 싶어요.','로그인 화면에서 ''비밀번호 찾기''를 클릭해주세요.<br>사용 중인 계정을 입력하면 이메일을 통해 비밀번호 변경 링크를 보내드려요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (4,'[계정] 지금 계정에 있는 강의를 다른 계정으로 옮기고 싶어요.','현재 계정 간 강의 이동 서비스를 제공하고 있지 않아요.<br>강의 저작권에 문제가 발생할 수 있기 때문에 각 계정은 개별 사용자로 인식하고 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (5,'[서비스오류] 인프런 서비스 이용 중 오류가 발생해요.','인프런은 구글 크롬 브라우저에 최적화되어 있어요.<br>강의 시청, 결제 등 서비스 이용 중 오류가 발생할 경우 크롬 브라우저에서 다시 진행해 주세요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (6,'[강의] 수강 전인데 강의에 궁금한 점이 있어요.','강의를 결제하기 전, 궁금한 점이 있으실 경우 [수강 전 문의 게시판]을 통해 지식공유자님에게 직접 질문을 남길 수 있어요.<br><br>※ 수강 전 문의하기 기능을 설정한 강의에 한정하여 제공되니 꼭 확인해 주세요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (7,'[강의] 강의자료는 어디에서 확인하나요?','강의 동영상을 클릭 후 상단 바 혹은 목차 내에 구름 아이콘을 확인해 주세요.<br><br>※ 강의마다 강의자료가 위치한 섹션이 다르니 참고해 주세요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (8,'[강의] 수료증은 어떻게 발급받나요?','수료증은 강의를 100% 완강하신 후 강의 완료 버튼을 누르시면 자동으로 발급됩니다.<br><br>대시보드 > 🏆 My Certificate(증명서) 란에서 확인 가능하세요 :)');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (9,'[강의] 수강 중인 강의에 대한 증빙이 필요해요.','아직 강의를 100% 수강하지 못하셨다면 수강확인증을 통해 증빙이 가능해요.<br>대시보드 > 내 학습에서 수강확인증을 발급받으실 수 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (10,'[강의] 강의별 수강 기한이 궁금해요.','수강 기한은 강의 페이지 우측 배너에서 확인하실 수 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (11,'[강의] 나에게 딱 맞는 강의를 추천받고 싶어요.','어떤 강의를 수강할 지, 어떤 강의가 내게 필요한 지 고민 중이신가요?<br>학습자마다 선수 지식, 체감 난이도, 습득력 등 개인 역량이 모두 다르기 때문에 구체적인 강의 상담은 어려워요. 하지만 꼭 맞는 강의를 찾는 데에 도움이 될 만한 방법들을 알려드릴게요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (12,'[강의] 로드맵은 어떤 기능인가요?','로드맵이란 효율적이고 체계적인 학습을 위해 여러 강의를 난이도별&유형별로 엮어 큐레이션 해주는 기능이에요.<br>어떤 강의를 어떤 순서로 들어야 할지 막막할 때, 듣고 있는 강의의 다음 스텝에 대한 정보가 필요할 때 로드맵 기능을 활용해 보세요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (13,'[강의] 진도율을 초기화할 수 있나요?','진도율은 환불 및 수료증 발급과 밀접하게 관련된 정보로 초기화 기능이 제공되지 않아요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (14,'[강의] 동시 접속이 가능한가요?','수강 중 동시 접속은 제한하고 있어요.<br>즉, 학습페이지는 1대에서만 접속할 수 있으며 다른 곳에서 동시 학습할 경우 학습이 제한될 수 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (15,'[강의] 기기 등록은 몇 대까지 가능한가요?','기기 등록은 한 계정당 최대 3대까지 가능해요.<br>현재 내가 등록된 기기를 확인할 수 있는 페이지는 별도로 제공하고 있지 않습니다.<br>로그인 시점마다 기기 등록이 갱신되어 별도의 초기화 요청 없이도 이용 가능합니다.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (16,'[강의] 수강평은 어디서 작성할 수 있나요?','일정 진도율 (5%)을 넘으면 학습페이지 하단에 ''수강평 등록'' 창이 나타나요.<br>혹은 강의 동영상 상단에 ''수강평 작성하기'' 버튼을 클릭 후 수강평 작성이 가능해요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (17,'[결제] 현금영수증을 발행하고 싶어요.','현금영수증은 무통장입금(가상 계좌) 결제 화면에서 직접 신청이 가능해요.<br><br>결제를 완료하셨다면 영업일 2~5일 후 대시보드 > 구매 내역 > 영수증에서 직접 확인하실 수 있습니다.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (18,'[결제] 구매 영수증은 어떻게 확인하나요?','구매 영수증 및 거래명세서는 대시보드> 구매 내역에서 직접 확인이 가능해요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (19,'[결제] 전자세금계산서 발급이 가능한가요?','인프런은 PG사를 통한 가상 계좌 결제방식을 제공하고 있어 전자세금계산서 발행이 불가능해요.<br>다만, 전자세금계산서와 법적 증빙 효력이 동일한 현금영수증을 발행 받아보실 수 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (20,'[결제] 강의를 환불하고 싶어요.','수강 신청한 유료강의를 환불하고 싶으신가요?<br>대시보드 > 구매내역에서 환불을 원하는 ''주문명''를 클릭하시면 직접 환불하실 수 있어요. <br><br>단, 환불 조건에 부합하지 않는 경우 취소 버튼이 비활성화되니 참고해 주세요!');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (21,'[결제] KDC / 내일배움카드 등 국비 교육비를 지원받을 수 있나요?','인프런은 고용노동부 국비 기관 기준에 맞지 않아 국비 교육비 지원이 불가능합니다.<br>국비 교육 기관 인증을 받으려면  고용노동부가 정해둔 획일화된 가이드라인을 맞춰야 되는데, 인프런은 다양한 직무 콘텐츠를 담는 곳이기 때문에 승인이 어렵습니다.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (22,'[할인] 현재 진행 중인 할인이 궁금해요.','상시로 진행 중인 할인 이벤트는 아래와 같아요.<br><br>1. 카카오톡 플러스친구 맺기<br>2. 인스타 팔로우& 쿠폰코드 찾기');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (23,'[할인] 할인과 쿠폰은 중복 적용이 안되나요?','모든 할인 및 쿠폰은 하나만 적용이 가능해요.<br>할인 중인 강의라면 강의 할인이 우선 적용되어 쿠폰 적용이 안되는 점 참고해 주세요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (24,'[할인] 15% 소속인증 할인이 궁금해요.','현재 사용 중인 학교 (고등학교, 대학교 등) 또는 회사 메일이 있는 경우 참여하실 수 있어요.');
Insert into LEARN.FAQ (FAQ_NO,FAQ_QUESTION,FAQ_ANSWER) values (25,'[멘토링] 인프런 멘토링 서비스가 궁금해요.','인프런 멘토링은 멘토와 멘티를 연결해 약속을 잡아주는 기능이에요.<br><br>커리어 상담부터 코드, 포트폴리오 리뷰까지 멘토가 필요할 땐 인프런 멘토링을 이용해 보세요.<br>지식공유자와 학습자가 더 쉽게 지식을 나누며 성장할 수 있답니다.');
